import Products from './components/Products/';
import { productData, yearDataTwo } from './components/Products/data';
import ListPage from './components/containers/ListPage';
import Navbar from './components/NavBar/NavBar';
import Footer from './components/Footer/footer';

import InnovYear from './components/InnovYear';
import { yearData } from './components/InnovYear/data';
import App from './App';
import { useState , useEffect} from "react";
import Axios from 'axios';


function Home() {

    const [bestInnov, setBestInnovator] = useState([]);

    const GetTheBestInnovator = () => {
      console.log('we here at get likes');
      Axios.get("http://localhost:3000/bestInnovator").then((response) => {
        console.log(response);
        setBestInnovator(response.data);
      });
    }

    useEffect(() => {
        let ignore = false;
        if (!ignore)  GetTheBestInnovator()
        return () => { ignore = true; }
        },[]);
  

    return (
        <Route>
            <Navbar />
            <Hero />
            <GlobalStyle />
            {/* <InnovYear heading='THE INNOVATOR OF THE YEAR' data={bestInnov} /> */}
            <Products heading='THE INNOVATOR OF THE YEAR' data={productData} />
{/*  */}
            {/* {bestInnov.map((val, key ) => {
              //  return <div> {val.innovator_fname} </div>
              return <Card
                title={val.innovator_fname + ' ' + val.innovator_lname}
                date={dateFormat(val.innovator_date, "mm/dd/yyyy")}
                image={val.innovator_picture}
                description={val.innovator_story}
                liked={val.innovator_like}
                likeCount={val.innovator_like}
              />
             })} */}
             {/* <div className="mainContCard">{cardContent.map(createCard)}</div> */}

{/*  */}
            <Products heading='FEATURED' heading1='INNOVATORS' data={yearDataTwo} />


  <button onClick={GetTheBestInnovator}>Show btt </button>
  {bestInnov.map((val, key ) => {
               return <div> {val.innovator_fname} </div>
             })}


            {/*  */}
            {/* <Feature /> */}
            {/* <Route path='/' exact component={Innov} /> */}
            <Footer />
        </Route>


    );
}


export default Home;
